import * as React from "react";
import {
  // ListItem,
  // ListItemIcon,
  Box,
  Drawer,
  Toolbar,
  // List,
  Modal,
  TextField,
} from "@mui/material";
import "./Chat.css";
import CreditCard from "../../Images/CreditCard.png";
import Promptpay from "../../Images/Promptpay.png";
import Bank_transfer from "../../Images/Banktransfer.png";
import { LocalizationProvider } from "@mui/x-date-pickers/LocalizationProvider";
import { MobileDatePicker } from "@mui/x-date-pickers/MobileDatePicker";
import { AdapterLuxon } from "@mui/x-date-pickers/AdapterLuxon";
import { BsArrowLeftShort, BsX } from "react-icons/bs";
import DrawerInHome from "../Drawer/DrawerCustomer";
import Navbars from "../Navbar/navbarHome2";
import Status from "./status";
import { Paper, IconButton, InputBase } from "@mui/material";
import SearchIcon from "@mui/icons-material/Search";
import { updateOrder } from "../../slices/auth";
import {
  // FaHome,
  // FaLanguage,
  // FaShoppingCart,
  FaTelegramPlane,
  FaUserCircle,
} from "react-icons/fa";

import {
  // IoChatbubblesSharp,
  // IoNotifications,
  IoDocumentText,
} from "react-icons/io5";
import { useNavigate, useLocation } from "react-router-dom";
import { PaymentEN, PaymentDE, PaymentTH } from "../Data/DataLanguage";

//test
import { useDispatch, useSelector } from "react-redux";
import axios from "axios";
import { createOrder } from "../../slices/auth";
import moment from "moment";

// const drawerWidth = 100;
export default function Chat() {
  const dispatch = useDispatch();
  const auth = useSelector((state) => state.auth);

  const [createQ, setCreateQ] = React.useState(false);
  const [payOpen, setPayOpen] = React.useState(false);
  const [scanQR, setScanQR] = React.useState(false);
  const [step, setStep] = React.useState(0);

  const [data, setData] = React.useState([]);
  const [openChat, setOpenChat] = React.useState("");

  const [price, setPrice] = React.useState("");


  const [order, setOrder] = React.useState({
    Date: "",
    Translator_name: "",
    Customer_name: "",
    Customers_will_get: "",
    Job_description: "",
    Deadline: "",
    Number_of_edits: "",
    Price: "",
    Status: "",
    Send_to: "",
    Review: "",
  });

  const navigate = useNavigate();
  const location = useLocation();
  let Doc = location?.state?.languages;
  let Doc2 = location?.state?.name;
  console.log(Doc2);
  let Value = auth?.token;

  React.useEffect(() => {
    if (Value) {
      console.log("value :", Value);
    }
    // else {
    //   navigate("/Login");
    // }
  }, [Value]);

  const handleClose = () => {
    setCreateQ(false);
    setPayOpen(false);
    setScanQR(false);
  };

  const Pay_Open = () => {
    dispatch(createOrder(order));
    setPayOpen(true);
    setStep(1);
    setCreateQ(false);
  };
  const handleOpen = () => {
    setPayOpen(false);
    setCreateQ(true);
  };
  const handleOpen_ScanQR = () => {
    console.log("test", openChat);
    // setOrder({ ...order, Date: openChat?.Date });
    // setOrder({ ...order, Translator_name: openChat?.Translator_name });
    // setOrder({ ...order, Customer_name: openChat?.Customer_name });
    // setOrder({ ...order, Customers_will_get: openChat?.Customers_will_get });
    // setOrder({ ...order, Job_description: openChat?.Job_description });
    // setOrder({ ...order, Deadline: openChat?.Deadline });
    // setOrder({ ...order, Number_of_edits: openChat?.Number_of_edits });
    // setOrder({ ...order, Price: openChat?.Price });
    // setOrder({ ...order, Send_to: openChat?.Send_to });
    // setOrder({ ...order, Review: openChat?.Review });
    setOrder({ ...order, Status: "2" });
    setScanQR(true);
  };
  const handleClose_scanQR = () => {
    console.log("hi", order);
    dispatch(updateOrder(order));
    setCreateQ(false);
    setPayOpen(false);
    setScanQR(false);
  };

  const Create_Quotation = () => {
    // dispatch(getOrder());
    setCreateQ(true);
  };

  const name = { Customer_name: auth?.name };
  const url = "http://localhost:3001/api";

  const setDataOrder = (i) => {
    console.log(i);
    const Day_List = i?.map((item, index) => {
      try {
        const formattedDate = moment(item?.Date).calendar();
        let formattedDate2 = moment(item?.Deadline).format("ll");
        return {
          id: item?._id,
          orderID: index,
          Translator_name: item?.Translator_name,
          orderedDate: formattedDate,
          Status: item?.Status,
          Price: item?.Price,
          Deadline: formattedDate2,
          Customer_name: item?.Customer_name,
          Customers_will_get: item?.Customers_will_get,
          Job_description: item?.Job_description,
          Number_of_edits: item?.Number_of_edits,
          Send_to: item?.Send_to,
          Review: item?.Review,
          De
        };
      } catch (e) {
        console.error(e);
        return null;
      }
    });
    setData(Day_List);
  };

  const getOrder = async (values) => {
    try {
      const token = await axios.get(`${url}/getOrder`, {
        params: { Customer_name: values?.Customer_name },
      });
      await console.log(token?.data);

      setDataOrder(token?.data);
    } catch (error) {
      if (error.response && error.response.status === 404) {
        throw new Error("Translator not found");
      } else if (error.response && error.response.status === 500) {
        throw new Error("Internal server error");
      } else if (error.response && error.response.status === 400) {
        throw new Error("Bad request");
      } else {
        throw new Error("Something went wrong");
      }
      // return rejectWithValue(error.response.data);
      // console.log(error.response);
    }
  };

  React.useEffect(() => {
    if (Value) {
      console.log("value :", Value);
    } else {
      navigate("/Login");
    }

    getOrder(name);
  }, []);
  console.log("openChat:", openChat);
  return (
    <div className="App-body">
      <header className="App-header">
        {Doc === "English" ? (
          <Navbars
            navigate={navigate}
            languages="English"
            dispatch={dispatch}
          />
        ) : Doc === "Thai" ? (
          <Navbars navigate={navigate} languages="Thai" dispatch={dispatch} />
        ) : Doc === "German" ? (
          <Navbars navigate={navigate} languages="German" dispatch={dispatch} />
        ) : (
          <Navbars
            navigate={navigate}
            languages="English"
            dispatch={dispatch}
          />
        )}
      </header>
      <Box sx={{ display: "flex", width: "100% " }}>
        <DrawerInHome languages={Doc} value={Value} />
        <>
          <Box component="main">
            <div
              style={{
                width: 300,
                position: "fixed",
                height: "-webkit-fill-available",
                borderRight: "1px solid rgba(0, 0, 0, 0.12)",
              }}
            >
              <div style={{ position: "absolute", top: 15, left: 25 }}>
                <p style={{ color: "#353535", fontSize: 30, fontWeight: 700 }}>
                  Chat
                </p>

                <Paper
                  component="form"
                  sx={{
                    p: "2px 4px",
                    display: "flex",
                    alignItems: "center",
                    width: 250,
                    borderRadius: 3,
                    backgroundColor: "#FFFFF",
                    boxShadow: "none",
                    marginTop: 2,
                    border: "1px #e5e5e5 solid",
                  }}
                >
                  <IconButton
                    type="submit"
                    sx={{ p: "10px", color: "#034D82" }}
                    aria-label="search"
                  >
                    <SearchIcon style={{ color: "#BFBFBF" }} />
                  </IconButton>
                  <InputBase
                    sx={{ ml: 1, flex: 1 }}
                    placeholder="Search"
                    inputProps={{ "aria-label": "search google maps" }}
                  />
                </Paper>

                <div
                  style={{
                    color: "#353535",
                    fontWeight: 500,
                    fontSize: 18,
                  }}
                >
                  {data.map((i) => (
                    <button
                      style={{
                        background: "#FFFFFF",
                        width: 250,
                        borderRadius: 12,
                        padding: 7,
                        border: "1px #E5E5E5 solid",
                        marginTop: 15,
                      }}
                      key={i.id}
                      onClick={() => setOpenChat(i)}
                    >
                      <div style={{ float: "left" }}>
                        <FaUserCircle
                          alt="avatar"
                          style={{
                            width: 35,
                            height: "auto",
                            margin: 10,
                            display: "block",
                            color: "#3333",
                          }}
                        />
                      </div>
                      <div style={{ float: "left", paddingTop: 7 }}>
                        <p>{i?.Translator_name}</p>
                      </div>
                      <div style={{ textAlign: "end" }}>
                        <p
                          style={{
                            color: "#808080",
                            fontWeight: 0,
                            fontSize: 12,
                            marginLeft: 15,
                          }}
                        >
                          {i?.orderedDate}
                        </p>
                      </div>
                      <div
                        style={{
                          position: "relative",
                          right: 50,
                          paddingTop: 15,
                        }}
                      >
                        <p
                          style={{
                            color: "#808080",
                            fontWeight: 0,
                            fontSize: 15,
                          }}
                        >
                          You : I want to ...
                        </p>
                      </div>
                    </button>
                  ))}
                </div>
              </div>
            </div>
            {openChat === "" ? (
              <></>
            ) : (
              <div
                style={{
                  width: "52%",
                  position: "fixed",
                  height: "-webkit-fill-available",
                  background: "#FFFFFF",
                  left: 400,
                  border: "1px #e5e5e5 solid",
                  borderLeft: "0px solid",
                }}
              >
                <div
                  style={{
